$(document).ready(function () {
  $(".burger").click(function () {
    if ($(".nav-ul").hasClass("mobile")) {
      $(".nav-ul").removeClass("mobile");
    } else {
      $(".nav-ul").addClass("mobile");
    }
    if ($(".lines").hasClass("opened")) {
      $(".lines").removeClass("opened");
    } else {
      $(".lines").addClass("opened");
    }
  });
});
